select.if:
SELECT b.rec_id, ifnull(r.image ,b.banner), b.name, b.cover FROM `book` b LEFT JOIN recommend r ON b.rec_id = r.recommend_id WHERE r.recommend_type = #{recommendType}
AND r.`menu`= #{menu}
ORDER BY r.create_time DESC

SELECT b.rec_id, b.banner, b.name, '' cover FROM `lesson` b LEFT JOIN recommend r ON b.rec_id = r.recommend_id WHERE r.recommend_type = #{recommendType}
AND r.`menu`= #{menu}
ORDER BY r.create_time DESC

mapper.select:
SELECT a.`name` oneLevelName, b.`name` twoLevelName, c.`name` threeLevelName, d.`name`, d.rec_id itemId, d.publish_status publishStatus FROM category a JOIN category b ON a.rec_id = b.pid JOIN category c ON b.rec_id = c.pid JOIN relevance_item e ON c.rec_id = e.category_id AND e.item_type = #{recommendType} JOIN recommend f ON e.item_id = f.recommend_id
AND f.`menu`= #{menu}
JOIN book d ON d.rec_id = f.recommend_id WHERE a.`level` = 1 ORDER BY a.name, b.name, c.name
